import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/HealthCheck.css';

const HealthCheckForm = ({ onSubmit, initialValues = {}, isEditing = false }) => {
  const defaultValues = {
    name: '',
    duration_days: 0,
    duration_hours: 0,
    duration_minutes: 5,
    grace_days: 0,
    grace_hours: 0,
    grace_minutes: 5,
    channel: '',
    recipient: '',
    ...initialValues
  };

  const [formData, setFormData] = useState(defaultValues);
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });

    // Clear error for this field when user edits it
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ''
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    // Validate duration values are non-negative
    if (formData.duration_days < 0) {
      newErrors.duration_days = 'Days must be non-negative';
    }
    if (formData.duration_hours < 0) {
      newErrors.duration_hours = 'Hours must be non-negative';
    }
    if (formData.duration_minutes < 0) {
      newErrors.duration_minutes = 'Minutes must be non-negative';
    }

    // Validate grace period values are non-negative
    if (formData.grace_days < 0) {
      newErrors.grace_days = 'Days must be non-negative';
    }
    if (formData.grace_hours < 0) {
      newErrors.grace_hours = 'Hours must be non-negative';
    }
    if (formData.grace_minutes < 0) {
      newErrors.grace_minutes = 'Minutes must be non-negative';
    }

    // Validate at least one of the duration fields is greater than 0
    if (formData.duration_days === 0 && formData.duration_hours === 0 && formData.duration_minutes === 0) {
      newErrors.duration = 'Duration must be greater than 0';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      await onSubmit(formData);
    } catch (err) {
      // If onSubmit throws, we'll display the error 
      console.error('Error submitting form:', err);

      // Check if response contains validation errors
      if (err.message && err.message.includes(':')) {
        const errorParts = err.message.split(';');
        const newErrors = {};

        errorParts.forEach(part => {
          const [key, message] = part.split(':').map(s => s.trim());
          if (key && message) {
            newErrors[key] = message;
          }
        });

        setErrors(newErrors);
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form className="health-check-form" onSubmit={handleSubmit}>
      {/* Basic Information Section */}
      <div className="form-section">
        <h3>Basic Information</h3>
        <div className="form-group">
          <label htmlFor="name">Name *</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className={errors.name ? 'error' : ''}
            required
          />
          {errors.name && <div className="error-message">{errors.name}</div>}
          <small>A descriptive name for your health check</small>
        </div>
      </div>

      {/* Notification Settings Section */}
      <div className="form-section">
        <h3>Notification Settings</h3>
        <div className="form-group">
          <label htmlFor="channel">Notification Channel</label>
          <select
            id="channel"
            name="channel"
            value={formData.channel}
            onChange={handleChange}
          >
            <option value="">None</option>
            <option value="email">Email</option>
            <option value="webhook">Webhook</option>
            <option value="slack">Slack</option>
          </select>
          <small>How you want to be notified when the check fails</small>
        </div>

        <div className="form-group">
          <label htmlFor="recipient">Recipient</label>
          <input
            type="text"
            id="recipient"
            name="recipient"
            value={formData.recipient}
            onChange={handleChange}
            placeholder="Email address or webhook URL"
          />
          <small>Where to send notifications (email address, webhook URL, etc.)</small>
        </div>
      </div>

      {/* Timing Settings Section */}
      <div className="form-section">
        <h3>Timing Settings</h3>

        {/* Duration */}
        <div className="form-group">
          <label>Duration</label>
          {errors.duration && <div className="error-message">{errors.duration}</div>}
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="duration_days">Days</label>
              <input
                type="number"
                id="duration_days"
                name="duration_days"
                min="0"
                value={formData.duration_days}
                onChange={handleChange}
                className={errors.duration_days ? 'error' : ''}
              />
              {errors.duration_days && <div className="error-message">{errors.duration_days}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="duration_hours">Hours</label>
              <input
                type="number"
                id="duration_hours"
                name="duration_hours"
                min="0"
                value={formData.duration_hours}
                onChange={handleChange}
                className={errors.duration_hours ? 'error' : ''}
              />
              {errors.duration_hours && <div className="error-message">{errors.duration_hours}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="duration_minutes">Minutes</label>
              <input
                type="number"
                id="duration_minutes"
                name="duration_minutes"
                min="0"
                value={formData.duration_minutes}
                onChange={handleChange}
                className={errors.duration_minutes ? 'error' : ''}
              />
              {errors.duration_minutes && <div className="error-message">{errors.duration_minutes}</div>}
            </div>
          </div>
          <small>How often the service should ping the health check</small>
        </div>

        {/* Grace Period */}
        <div className="form-group">
          <label>Grace Period</label>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="grace_days">Days</label>
              <input
                type="number"
                id="grace_days"
                name="grace_days"
                min="0"
                value={formData.grace_days}
                onChange={handleChange}
                className={errors.grace_days ? 'error' : ''}
              />
              {errors.grace_days && <div className="error-message">{errors.grace_days}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="grace_hours">Hours</label>
              <input
                type="number"
                id="grace_hours"
                name="grace_hours"
                min="0"
                value={formData.grace_hours}
                onChange={handleChange}
                className={errors.grace_hours ? 'error' : ''}
              />
              {errors.grace_hours && <div className="error-message">{errors.grace_hours}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="grace_minutes">Minutes</label>
              <input
                type="number"
                id="grace_minutes"
                name="grace_minutes"
                min="0"
                value={formData.grace_minutes}
                onChange={handleChange}
                className={errors.grace_minutes ? 'error' : ''}
              />
              {errors.grace_minutes && <div className="error-message">{errors.grace_minutes}</div>}
            </div>
          </div>
          <small>Additional time to wait before alerting after a missed ping</small>
        </div>
      </div>

      <div className="form-actions">
        <button 
          type="submit" 
          className="btn btn-primary" 
          disabled={isSubmitting}
        >
          {isSubmitting ? 'Saving...' : isEditing ? 'Update Health Check' : 'Create Health Check'}
        </button>
      </div>
    </form>
  );
};

export default HealthCheckForm;
