import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/AuthPages.css';
import logo from '../assets/Poly-Agent_logo.jpeg';

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    name: '',
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const validateForm = () => {
    // Check all fields are filled
    if (!formData.name.trim() || !formData.username.trim() || 
        !formData.email.trim() || !formData.password.trim() || 
        !formData.confirmPassword.trim()) {
      setError('Please fill in all fields');
      return false;
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError('Please enter a valid email address');
      return false;
    }

    // Check password length
    if (formData.password.length < 8) {
      setError('Password must be at least 8 characters long');
      return false;
    }

    // Check passwords match
    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    if (!validateForm()) {
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      // First, get CSRF token
      const csrfResponse = await fetch('/checks/register/?no_redirect=true', {
        method: 'GET',
        credentials: 'include'
      });

      // Extract CSRF token from the HTML
      const csrfHtml = await csrfResponse.text();
      const csrfMatch = csrfHtml.match(/name="csrfmiddlewaretoken" value="([^"]+)"/);

      if (!csrfMatch) {
        throw new Error('Could not retrieve CSRF token');
      }

      const csrfToken = csrfMatch[1];

      // Prepare form data for registration
      const registerFormData = new FormData();
      registerFormData.append('name', formData.name);
      registerFormData.append('username', formData.username);
      registerFormData.append('email', formData.email);
      registerFormData.append('password', formData.password);
      registerFormData.append('csrfmiddlewaretoken', csrfToken);

      // Send registration request
      const registerResponse = await fetch('/checks/register/', {
        method: 'POST',
        credentials: 'include',
        body: registerFormData,
        headers: {
          'X-CSRFToken': csrfToken,
          'X-React-App': 'true'
        }
      });

      if (registerResponse.ok) {
        // Check if registration was successful
        if (registerResponse.url.includes('dashboard')) {
          // Registration successful and user is logged in
          navigate('/dashboard');
        } else {
          // Parsing the response to get more detailed error messages
          const responseText = await registerResponse.text();

          if (responseText.includes('username already exists')) {
            setError('Username already exists');
          } else if (responseText.includes('email already exists')) {
            setError('Email address already in use');
          } else {
            // Navigate to login page on successful registration
            navigate('/login', { state: { message: 'Registration successful! Please log in.' } });
          }
        }
      } else {
        // Handle registration errors
        const responseData = await registerResponse.json();
        setError(responseData.error || 'Registration failed. Please try again.');
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError('An error occurred during registration. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-logo-container">
          <img src={logo} alt="Poly-Agent Logo" className="auth-logo" />
        </div>

        <h1 className="auth-title">Create an Account</h1>
        <p className="auth-subtitle">Join Poly-Agent to monitor your services and analyze GitHub data</p>

        {error && <div className="auth-error">{error}</div>}

        <form className="auth-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Full Name</label>
            <div className="input-with-icon">
              <i className="fas fa-user input-icon"></i>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Enter your full name"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="username">Username</label>
            <div className="input-with-icon">
              <i className="fas fa-at input-icon"></i>
              <input
                type="text"
                id="username"
                name="username"
                value={formData.username}
                onChange={handleChange}
                placeholder="Choose a username"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <div className="input-with-icon">
              <i className="fas fa-envelope input-icon"></i>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email address"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="input-with-icon">
              <i className="fas fa-lock input-icon"></i>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Create a password (min. 8 characters)"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <div className="input-with-icon">
              <i className="fas fa-lock input-icon"></i>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                placeholder="Confirm your password"
                required
              />
            </div>
          </div>

          <button
            type="submit"
            className="auth-button"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="spinner"></span>
                Creating Account...
              </>
            ) : (
              'Create Account'
            )}
          </button>
        </form>

        <div className="auth-links">
          <p>Already have an account?</p>
          <Link to="/login" className="auth-link primary">Log In</Link>
        </div>

        <div className="auth-home-link">
          <Link to="/" className="back-to-home">
            <i className="fas fa-arrow-left"></i> Back to Home
          </Link>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;
