import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/AuthPages.css';
import logo from '../assets/Poly-Agent_logo.jpeg';

const LoginPage = () => {
  const [userInput, setUserInput] = useState(''); // Can be either username or email
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!userInput.trim() || !password.trim()) {
      setError('Please enter all fields');
      return;
    }

    setIsLoading(true);
    setError('');

    try {
      // First, get CSRF token
      const csrfResponse = await fetch('/checks/login/?no_redirect=true', {
        method: 'GET',
        credentials: 'include'
      });

      // Extract CSRF token from the HTML
      const csrfHtml = await csrfResponse.text();
      const csrfMatch = csrfHtml.match(/name="csrfmiddlewaretoken" value="([^"]+)"/);

      if (!csrfMatch) {
        throw new Error('Could not retrieve CSRF token');
      }

      const csrfToken = csrfMatch[1];

      // Prepare form data for login
      const formData = new FormData();

      // Determine if input is email or username
      const isEmail = userInput.includes('@');
      formData.append(isEmail ? 'email' : 'username', userInput);
      formData.append('password', password);
      formData.append('csrfmiddlewaretoken', csrfToken);

      // Send login request
      const loginResponse = await fetch('/checks/login/', {
        method: 'POST',
        credentials: 'include',
        body: formData,
        headers: {
          'X-CSRFToken': csrfToken,
          'X-React-App': 'true'
        }
      });

      if (loginResponse.ok) {
        // Check if we got redirected to dashboard (success) or still on login (failure)
        if (loginResponse.url.includes('dashboard')) {
          // Login successful
          navigate('/dashboard');
        } else {
          // Invalid credentials
          setError('Invalid username/email or password');
        }
      } else {
        throw new Error('Login failed');
      }
    } catch (err) {
      console.error('Login error:', err);
      setError('An error occurred during login. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-logo-container">
          <img src={logo} alt="Poly-Agent Logo" className="auth-logo" />
        </div>

        <h1 className="auth-title">Login to Poly-Agent</h1>
        <p className="auth-subtitle">Access your health checks and GitHub analytics</p>

        {error && <div className="auth-error">{error}</div>}

        <form className="auth-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="userInput">Username or Email</label>
            <div className="input-with-icon">
              <i className="fas fa-user input-icon"></i>
              <input
                type="text"
                id="userInput"
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                placeholder="Enter your username or email"
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <div className="input-with-icon">
              <i className="fas fa-lock input-icon"></i>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </div>
          </div>

          <button
            type="submit"
            className="auth-button"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="spinner"></span>
                Logging in...
              </>
            ) : (
              'Login'
            )}
          </button>
        </form>

        <div className="auth-links">
          <Link to="/forgot-password" className="auth-link">Forgot Password?</Link>
          <div className="auth-separator" />
          <p>Don't have an account?</p>
          <Link to="/register" className="auth-link primary">Create Account</Link>
        </div>

        <div className="auth-home-link">
          <Link to="/" className="back-to-home">
            <i className="fas fa-arrow-left"></i> Back to Home
          </Link>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
