import React, { useState, useEffect } from 'react';
import '../styles/HealthChecks.css';

const HealthChecks = () => {
  const [healthChecks, setHealthChecks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [summary, setSummary] = useState(null);

  useEffect(() => {
    // Fetch health checks when component mounts
    fetchHealthChecks();
    fetchSummary();
  }, []);

  const fetchHealthChecks = async () => {
    try {
      const response = await fetch('/health/api/checks/', {
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setHealthChecks(data.results || []);
      } else {
        throw new Error('Failed to fetch health checks');
      }
    } catch (err) {
      console.error('Error fetching health checks:', err);
      setError('Failed to load health checks. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchSummary = async () => {
    try {
      const response = await fetch('/health/api/checks/summary/', {
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setSummary(data);
      }
    } catch (err) {
      console.error('Error fetching summary:', err);
      // We don't set error here to not disrupt the main content
    }
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return 'Never';
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  const getStatusClass = (isUp) => {
    return isUp ? 'status-up' : 'status-down';
  };

  const formatResponseTime = (time) => {
    if (!time) return 'N/A';
    return `${time.toFixed(2)} ms`;
  };

  if (isLoading) {
    return (
      <div className="health-checks-loading">
        <div className="spinner"></div>
        <p>Loading health checks...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="health-checks-error">
        <h3>Error</h3>
        <p>{error}</p>
        <button onClick={fetchHealthChecks}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="health-checks-container">
      {summary && (
        <div className="health-checks-summary">
          <h2>Health Checks Overview</h2>
          <div className="summary-stats">
            <div className="summary-stat">
              <span className="stat-value">{summary.total_checks}</span>
              <span className="stat-label">Total Checks</span>
            </div>
            <div className="summary-stat">
              <span className="stat-value">{summary.active_checks}</span>
              <span className="stat-label">Active Checks</span>
            </div>
            <div className="summary-stat">
              <span className="stat-value">{summary.up_checks}</span>
              <span className="stat-label">Up</span>
            </div>
            <div className="summary-stat">
              <span className="stat-value">{summary.down_checks}</span>
              <span className="stat-label">Down</span>
            </div>
            <div className="summary-stat">
              <span className="stat-value">{summary.health_percentage.toFixed(1)}%</span>
              <span className="stat-label">Health</span>
            </div>
          </div>
        </div>
      )}

      <div className="health-checks-header">
        <h2>Health Checks</h2>
        <button className="create-check-btn">Create New Check</button>
      </div>

      {healthChecks.length === 0 ? (
        <div className="no-health-checks">
          <p>You don't have any health checks yet.</p>
          <button className="create-check-btn">Create Your First Check</button>
        </div>
      ) : (
        <div className="health-checks-list">
          {healthChecks.map(check => (
            <div key={check.id} className={`health-check-card ${getStatusClass(check.is_up)}`}>
              <div className="check-header">
                <h3>{check.name}</h3>
                <span className={`status-badge ${getStatusClass(check.is_up)}`}>
                  {check.is_up ? 'UP' : 'DOWN'}
                </span>
              </div>

              <div className="check-details">
                <div className="check-detail">
                  <span className="detail-label">Last Ping:</span>
                  <span className="detail-value">{formatDateTime(check.last_ping)}</span>
                </div>

                <div className="check-detail">
                  <span className="detail-label">Avg Response:</span>
                  <span className="detail-value">{formatResponseTime(check.avg_response_time)}</span>
                </div>

                <div className="check-detail">
                  <span className="detail-label">Total Pings:</span>
                  <span className="detail-value">{check.total_pings}</span>
                </div>

                <div className="check-detail">
                  <span className="detail-label">Ping URL:</span>
                  <span className="detail-value ping-url">{`${window.location.origin}/health/ping/${check.ping_url}/`}</span>
                </div>
              </div>

              <div className="check-actions">
                <button className="view-details-btn">View Details</button>
                <button className="ping-now-btn">Ping Now</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default HealthChecks;
