import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/HealthCheck.css';
import { 
  FaCheckCircle, 
  FaTimesCircle, 
  FaExclamationTriangle, 
  FaServer, 
  FaClock, 
  FaExternalLinkAlt,
  FaCopy,
  FaEdit,
  FaEye
} from 'react-icons/fa';
import { toast } from 'react-toastify';

const HealthCheckDashboard = () => {
  const [summary, setSummary] = useState({
    totalChecks: 0,
    activeChecks: 0,
    upChecks: 0,
    downChecks: 0,
    healthPercentage: 0,
    recentFailures: []
  });
  const [healthChecks, setHealthChecks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch summary data
        const summaryResponse = await fetch('/health/api/checks/summary/', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
        });

        if (!summaryResponse.ok) {
          throw new Error('Failed to fetch health check summary');
        }

        const summaryData = await summaryResponse.json();
        setSummary({
          totalChecks: summaryData.total_checks || 0,
          activeChecks: summaryData.active_checks || 0,
          upChecks: summaryData.up_checks || 0,
          downChecks: summaryData.down_checks || 0,
          healthPercentage: summaryData.health_percentage || 0,
          recentFailures: summaryData.recent_failures || []
        });

        // Fetch health checks list
        const checksResponse = await fetch('/health/api/checks/', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include',
        });

        if (!checksResponse.ok) {
          throw new Error('Failed to fetch health checks');
        }

        const checksData = await checksResponse.json();
        setHealthChecks(Array.isArray(checksData) ? checksData : checksData.results || []);
      } catch (err) {
        console.error('Error fetching health check data:', err);
        setError(err.message || 'An error occurred while fetching data');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const formatTime = (timeString) => {
    if (!timeString) return 'Never';
    const date = new Date(timeString);
    return date.toLocaleString();
  };

  const getStatusClass = (check) => {
    if (!check.is_active) return 'status-inactive';
    return check.is_up ? 'status-up' : 'status-down';
  };

  const getStatusText = (check) => {
    if (!check.is_active) return 'Inactive';
    return check.is_up ? 'Up' : 'Down';
  };

  const getStatusIcon = (check) => {
    if (!check.is_active) return <FaExclamationTriangle className="status-icon inactive" />;
    return check.is_up ? 
      <FaCheckCircle className="status-icon up" /> : 
      <FaTimesCircle className="status-icon down" />;
  };

  const formatResponseTime = (time) => {
    if (!time) return 'N/A';
    return `${time.toFixed(2)} ms`;
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
    toast.success('Copied to clipboard!');
  };

  const getUptimePercentage = (check) => {
    if (!check.total_pings) return 'N/A';
    const successRate = ((check.total_pings - (check.failed_pings || 0)) / check.total_pings) * 100;
    return `${successRate.toFixed(1)}%`;
  };

  const getLastStatusChange = (check) => {
    if (!check.last_ping) return 'Never';
    const lastPing = new Date(check.last_ping);
    const now = new Date();
    const diffInSeconds = Math.floor((now - lastPing) / 1000);

    if (diffInSeconds < 60) return `${diffInSeconds} seconds ago`;
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)} minutes ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)} hours ago`;
    return `${Math.floor(diffInSeconds / 86400)} days ago`;
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="spinner large"></div>
        <p>Loading health check summary...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <p className="error-message">Error: {error}</p>
        <p>Please try refreshing the page or contact support if the problem persists.</p>
      </div>
    );
  }

  return (
    <div className="health-dashboard">
      <div className="dashboard-section">
        <h2>Health Check Summary</h2>
        <div className="summary-stats">
          <div className="stat-card">
            <div className="stat-value">{summary.totalChecks}</div>
            <div className="stat-label">Total Checks</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">{summary.activeChecks}</div>
            <div className="stat-label">Active</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">{summary.upChecks}</div>
            <div className="stat-label">Up</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">{summary.downChecks}</div>
            <div className="stat-label">Down</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">{summary.healthPercentage.toFixed(1)}%</div>
            <div className="stat-label">Health Rate</div>
          </div>
        </div>
      </div>

      <div className="dashboard-section">
        <div className="section-header">
          <h2>Your Health Checks</h2>
          <Link to="/health-checks/create" className="create-btn">
            <span>+ Create New Check</span>
          </Link>
        </div>

        {healthChecks.length === 0 ? (
          <div className="empty-state">
            <div className="empty-state-content">
              <div className="empty-state-icon">
                <FaServer size={48} />
              </div>
              <h3>No health checks yet</h3>
              <p>Create your first health check to start monitoring your services</p>
              <Link to="/health-checks/create" className="primary-btn">
                Create Health Check
              </Link>
            </div>
          </div>
        ) : (
          <div className="health-checks-container">
            <div className="health-checks-header">
              <div className="header-cell name">Name</div>
              <div className="header-cell status">Status</div>
              <div className="header-cell url">URL</div>
              <div className="header-cell last-checked">Last Checked</div>
              <div className="header-cell uptime">Uptime</div>
              <div className="header-cell response">Response</div>
              <div className="header-cell actions">Actions</div>
            </div>

            <div className="health-checks-list">
              {healthChecks.map(check => (
                <div key={check.id} className={`health-check-row ${getStatusClass(check)}`}>
                  <div className="cell name">
                    <div className="name-wrapper">
                      {getStatusIcon(check)}
                      <span className="check-name" title={check.name}>{check.name}</span>
                    </div>
                  </div>

                  <div className="cell status">
                    <span className={`status-badge ${getStatusClass(check)}`}>
                      {getStatusText(check)}
                    </span>
                  </div>

                  <div className="cell url" title={check.url || 'Not specified'}>
                    {check.url ? (
                      <a 
                        href={check.url} 
                        target="_blank" 
                        rel="noopener noreferrer"
                        className="url-link"
                        onClick={(e) => e.stopPropagation()}
                      >
                        {check.url.replace(/^https?:\/\//, '').split('/')[0]}
                        <FaExternalLinkAlt className="external-icon" />
                      </a>
                    ) : 'N/A'}
                  </div>

                  <div className="cell last-checked" title={formatTime(check.last_ping)}>
                    {getLastStatusChange(check)}
                  </div>

                  <div className="cell uptime">
                    {getUptimePercentage(check)}
                  </div>

                  <div className="cell response">
                    {formatResponseTime(check.avg_response_time)}
                  </div>

                  <div className="cell actions">
                    <div className="action-buttons">
                      <button 
                        className="icon-btn" 
                        onClick={() => copyToClipboard(`${window.location.origin.replace('3000', '8000')}/health/ping/${check.ping_url}/`)}
                        title="Copy Ping URL"
                      >
                        <FaCopy />
                      </button>
                      <Link 
                        to={`/check/${check.id}`} 
                        className="icon-btn view-btn"
                        title="View Details"
                      >
                        <FaEye />
                      </Link>
                      <Link 
                        to={`/check/${check.id}/edit`} 
                        className="icon-btn edit-btn"
                        title="Edit Check"
                      >
                        <FaEdit />
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            <div className="health-checks-footer">
              <div className="showing-count">
                Showing {healthChecks.length} of {healthChecks.length} health checks
              </div>
              <div className="pagination">
                <button className="pagination-btn" disabled>Previous</button>
                <span className="page-info">Page 1 of 1</span>
                <button className="pagination-btn" disabled>Next</button>
              </div>
            </div>
          </div>
        )}
      </div>

      <div className="dashboard-section">
        <div className="section-header">
          <h2>Recent Failures</h2>
          <Link to="/failed-checks" className="view-all-link">View All</Link>
        </div>
        {summary.recentFailures?.length === 0 ? (
          <div className="empty-state">
            <p>No recent failures. All systems operational!</p>
          </div>
        ) : (
          <div className="failures-list">
            {(summary.recentFailures || []).slice(0, 3).map((failure, index) => (
              <div key={index} className="failure-item">
                <div className="failure-name">{failure.health_check?.name || 'Unknown Check'}</div>
                <div className="failure-time">Failed at: {formatTime(failure.failed_at)}</div>
                <Link to={`/check/${failure.health_check?.id}`} className="view-details-link">
                  View Details
                </Link>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="dashboard-actions">
        <Link to="/health-checks/create" className="dashboard-action-button create">
          Create New Health Check
        </Link>
        <Link to="/reports" className="dashboard-action-button">
          View Reports
        </Link>
      </div>
    </div>
  );
};

export default HealthCheckDashboard;
