import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/HealthCheck.css';

const HealthCheckForm = ({ onSubmit, initialValues = {}, isEditing = false }) => {
  const defaultValues = {
    name: '',
    description: '',
    url: '',
    method: 'GET',
    expected_status_code: 200,
    check_interval: 5,
    timeout: 30,
    retries: 1,
    is_active: true,
    alert_threshold: 1,
    ...initialValues
  };

  const [formData, setFormData] = useState(defaultValues);
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });

    // Clear error for this field when user edits it
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: ''
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    }

    if (formData.url && !isValidUrl(formData.url)) {
      newErrors.url = 'Please enter a valid URL';
    }

    if (formData.expected_status_code && 
        (formData.expected_status_code < 100 || formData.expected_status_code > 599)) {
      newErrors.expected_status_code = 'Status code must be between 100 and 599';
    }

    if (formData.check_interval <= 0) {
      newErrors.check_interval = 'Check interval must be greater than 0';
    }

    if (formData.timeout <= 0) {
      newErrors.timeout = 'Timeout must be greater than 0';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      await onSubmit(formData);
    } catch (err) {
      // If onSubmit throws, we'll display the error 
      console.error('Error submitting form:', err);

      // Check if response contains validation errors
      if (err.message && err.message.includes(':')) {
        const errorParts = err.message.split(';');
        const newErrors = {};

        errorParts.forEach(part => {
          const [key, message] = part.split(':').map(s => s.trim());
          if (key && message) {
            newErrors[key] = message;
          }
        });

        setErrors(newErrors);
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form className="health-check-form" onSubmit={handleSubmit}>
      <div className="form-section">
        <h3>Basic Information</h3>
        <div className="form-group">
          <label htmlFor="name">Name *</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className={errors.name ? 'error' : ''}
            required
          />
          {errors.name && <div className="error-message">{errors.name}</div>}
          <small>A descriptive name for your health check</small>
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
          />
          <small>Optional description to provide more details about this check</small>
        </div>
      </div>

      <div className="form-section">
        <h3>Request Configuration</h3>
        <div className="form-group">
          <label htmlFor="url">URL</label>
          <input
            type="url"
            id="url"
            name="url"
            value={formData.url}
            onChange={handleChange}
            className={errors.url ? 'error' : ''}
          />
          {errors.url && <div className="error-message">{errors.url}</div>}
          <small>The URL to check (e.g., https://example.com/api/health)</small>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="method">Method</label>
            <select 
              id="method"
              name="method"
              value={formData.method}
              onChange={handleChange}
            >
              <option value="GET">GET</option>
              <option value="POST">POST</option>
              <option value="HEAD">HEAD</option>
              <option value="OPTIONS">OPTIONS</option>
            </select>
            <small>HTTP method to use for the request</small>
          </div>

          <div className="form-group">
            <label htmlFor="expected_status_code">Expected Status Code</label>
            <input
              type="number"
              id="expected_status_code"
              name="expected_status_code"
              min="100"
              max="599"
              value={formData.expected_status_code}
              onChange={handleChange}
              className={errors.expected_status_code ? 'error' : ''}
            />
            {errors.expected_status_code && (
              <div className="error-message">{errors.expected_status_code}</div>
            )}
            <small>Status code that indicates success (default: 200)</small>
          </div>
        </div>
      </div>

      <div className="form-section">
        <h3>Monitoring Settings</h3>
        <div className="form-row">
          <div className="form-group">
            <label htmlFor="check_interval">Check Interval (minutes)</label>
            <input
              type="number"
              id="check_interval"
              name="check_interval"
              min="1"
              value={formData.check_interval}
              onChange={handleChange}
              className={errors.check_interval ? 'error' : ''}
            />
            {errors.check_interval && (
              <div className="error-message">{errors.check_interval}</div>
            )}
            <small>How often to perform the health check (in minutes)</small>
          </div>

          <div className="form-group">
            <label htmlFor="timeout">Timeout (seconds)</label>
            <input
              type="number"
              id="timeout"
              name="timeout"
              min="1"
              value={formData.timeout}
              onChange={handleChange}
              className={errors.timeout ? 'error' : ''}
            />
            {errors.timeout && (
              <div className="error-message">{errors.timeout}</div>
            )}
            <small>How long to wait before considering the request failed</small>
          </div>

          <div className="form-group">
            <label htmlFor="retries">Retries</label>
            <input
              type="number"
              id="retries"
              name="retries"
              min="0"
              value={formData.retries}
              onChange={handleChange}
            />
            <small>Number of retry attempts before marking as failed</small>
          </div>
        </div>

        <div className="form-group">
          <div className="checkbox-wrapper">
            <input
              type="checkbox"
              id="is_active"
              name="is_active"
              checked={formData.is_active}
              onChange={handleChange}
            />
            <label htmlFor="is_active" className="checkbox-label">Active</label>
          </div>
          <small>If unchecked, the health check will not run</small>
        </div>
      </div>

      <div className="form-section">
        <h3>Alerts</h3>
        <div className="form-group">
          <label htmlFor="alert_threshold">Alert Threshold</label>
          <input
            type="number"
            id="alert_threshold"
            name="alert_threshold"
            min="1"
            value={formData.alert_threshold}
            onChange={handleChange}
          />
          <small>Number of consecutive failures before sending an alert</small>
        </div>
      </div>

      <div className="form-actions">
        <button 
          type="submit" 
          className="btn btn-primary" 
          disabled={isSubmitting}
        >
          {isSubmitting ? 'Saving...' : isEditing ? 'Update Health Check' : 'Create Health Check'}
        </button>
      </div>
    </form>
  );
};

export default HealthCheckForm;
